    public static String LatLngToString(LatLng latLng) {
        return Double.toString(latLng.latitude) + "&" + Double.toString(latLng.longitude);
    }

    public static String giveLatLangName(LatLng latLng) {
        try {
            JSONObject reader = new JSONObject(json);
            Log.d("ERRORRRRRR",LatLngToString(latLng));
            return reader.getString(LatLngToString(latLng));
        } catch (JSONException e) {
            Log.d("ERRORRRRRR",e.toString());
            return LatLngToString(latLng);
        }
    }

